	public static final String SQL_QUERY_GET_CONFERMA_LETTURA_MESSAGGIO = "select t4.crivw9106 as codEdicola, t4.nomeriva9106 as ragioneSociale, t4.locariva9106 as localita, t2.letto9602 as messaggioLetto from tbl_9206 t1 inner join tbl_9107 t3 on t1.coddl9206 = t3.coddl9107 inner join tbl_9106 t4 on t1.crivw9206 = t4.crivw9106 left outer join tbl_9602 t2 on t2.coded9602 = t4.crivw9106 and t2.coddl9602 = t1.coddl9206 and t2.datam9602 = :dtMessaggio and t2.letto9602 = " + IGerivConstants.COD_MESSAGGIO_LETTO + " where t1.coddl9206 = :codFiegDl and (t1.dasos9206 is null or t1.dasos9206 > sysdate) $1 order by t2.letto9602 desc";

	public static final String SQL_QUERY_GET_VENDITE_RIEPILOGO_PER_ARGOMENTO = "select  /*+ ordered */ sum(imptot9626) as importo, sum(quanti9626*prnet9617) as importoNetto, sum(quanti9626) as copie, nvl(descr9217, 'ALTRI') as raggruppamento, 1 as ordine from tbl_9625,tbl_9626,tbl_9607,tbl_9606,tbl_9217,tbl_9206,tbl_9617 where (delet9625 is null or delet9625 = 0) and coddl9625 = :codDl and datve9625 between :dtIni and :dtFine and crivw9625 = :codEdicola and idven9626 = idven9625 and (delet9626 is null or delet9626 = 0) and idtn9626 is not null and coddl9607 = coddl9626 and idtn9607 = idtn9626 and coddl9606 = coddl9626 and cpu9606 = cpu9607 and coddl9217(+) = coddl9606 and segm9217(+) = argodl9606 and crivw9206=crivw9626 and coddl9617=coddl9626 and idtn9617=idtn9626 and gs9617=gs9206 :wcClauseUtente group by nvl(descr9217, 'ALTRI')";
	public static final String SQL_QUERY_GET_VENDITE_RIEPILOGO_PER_PERIODICITA = "select  /*+ ordered */ sum(imptot9626) as importo, sum(quanti9626*prnet9617) as importoNetto, sum(quanti9626) as copie, descr9216 as raggruppamento, 1 as ordine from tbl_9625,tbl_9626,tbl_9607,tbl_9606,tbl_9216,tbl_9206,tbl_9617 where (delet9625 is null or delet9625 = 0) and coddl9625 = :codDl and datve9625 between :dtIni and :dtFine and crivw9625 = :codEdicola and idven9626 = idven9625 and (delet9626 is null or delet9626 = 0) and idtn9626 is not null and coddl9607 = coddl9626 and idtn9607 = idtn9626 and coddl9606 = coddl9626 and cpu9606 = cpu9607 and tipop9216 = 1 and perio9216 = peridl9606 and crivw9206=crivw9626 and coddl9617=coddl9626 and idtn9617=idtn9626 and gs9617=gs9206 :wcClauseUtente group by descr9216";
	
	public static final String SQL_QUERY_GET_VENDITE_RIEPILOGO_PRODOTTI_VARI = "union select  /*+ ordered */ sum(prezzo9546 * qt9546) as importo, sum(0) as importoNetto, count(1) as copie, :label as raggruppamento, 2 as ordine from tbl_9545, tbl_9546 where (delet9545 is null or delet9545 = 0) and datar9545 between :dtIni and :dtFine and crivw9545 = :codEdicola and id9546 = id9545 and (delet9546 is null or delet9546 = 0) and idre9545 = " + IGerivConstants.INDICATORE_DOCUMENTO_EMESSO + " :wcProdotti having sum((prezzo9546 * qt9546)) > 0";
	public static final String SQL_QUERY_GET_VENDITE_RIEPILOGO_A_VALORE = "union select  /*+ ordered */ sum(imptot9626) as importo, sum(0) as importoNetto, count(1) as copie, :label as raggruppamento, 3 as ordine from tbl_9625,tbl_9626 where (delet9625 is null or delet9625 = 0) and coddl9625 = :codDl and datve9625 between :dtIni and :dtFine and crivw9625 = :codEdicola and idven9626 = idven9625 and (delet9626 is null or delet9626 = 0) and idtn9626 is null :wcClauseUtente having sum(impve9626) > 0";

	public static final String SQL_QUERY_GET_RICHIESTE_VARIAZIONI = "select pub1_.titolo9606 as titolo, pub1_.cpu9606 as codicePubblicazione, arg2_.descr9217 as argomento, per3_.descr9216 as periodicita, this_.dari9129 as dataRichiesta, this_.quar9129 as quantitaRichiesta, this_.moti9129 as motivoRichiesta, this_.itdl9129 as indicatoreTrasmessoDl, this_.datr9129 as dataUltimaTrasmissioneDl from tbl_9129 this_ inner join tbl_9606 pub1_ on (this_.coddl9129 = pub1_.coddl9606 and this_.cpu9129 = pub1_.cpu9606 and pub1_.coddl9606 in (:codFiegDl) and this_.crivw9129 in (:codEdicola)) left outer join tbl_9217 arg2_ on (pub1_.coddl9606 = arg2_.coddl9217 and pub1_.argodl9606 = arg2_.segm9217) left outer join tbl_9216 per3_ on (pub1_.peridl9606 = per3_.perio9216 and per3_.tipop9216 = 1)";
