	public static final String SQL_QUERY_GET_PREFERITI = "select titolo as titolo, sottoTitolo as sottoTitolo, numero as numeroCopertina from (select titolo9606 as titolo, sottot9607 as sottoTitolo, num9607 as numero from (select coddl, crivw, ccli, cpu,  usciteCliente, max(idtn9607) idtn from (select coddl9625 coddl, crivw9625 crivw, ccli9625 ccli, cpu9607 cpu, count(1) usciteCliente from tbl_9626 inner join tbl_9625 on idven9626 = idven9625 and (delet9625 is null or delet9625 = 0) inner join tbl_9607 on coddl9607 = :coddl and idtn9607 = idtn9626 where (delet9626 is null or delet9626 = 0) and coddl9626 = :coddl and crivw9626 = :codEdicola and ccli9625  = :codCliente  and datve9625 between (sysdate - :giorni) and sysdate  group by coddl9625, crivw9625, ccli9625, cpu9607) inner join tbl_9607 on coddl9607 = coddl and cpu9607 = cpu group by coddl, crivw, ccli, cpu, usciteCliente) inner join tbl_9606 on coddl9606 = coddl and cpu9606 = cpu inner join tbl_9607 on coddl9607 = coddl and idtn9607 = idtn where not exists (select null from tbl_9626,tbl_9625 where (delet9625 is null or delet9625 = 0) and (delet9626 is null or delet9626 = 0) and coddl9626=coddl and crivw9626=crivw and idtn9626=idtn and idven9625=idven9626 and ccli9625=ccli) and P_BOLLA.GET_GIACENZA(coddl, crivw, idtn, :dataStorico) > 0 order by ((select count(1) from tbl_9607 where coddl9607 = coddl and cpu9607 = cpu and datausc9607 between (sysdate - :giorni) and sysdate) / usciteCliente) asc) where rownum <= :maxResults";
