	//public static final String SQL_QUERY_GET_UTLIME_PUBBLICAZIONI_PER_VENDITE_LOCALI = "select idtn9607 as idtn, barcode9607 as barcode, prezzo9607 as prezzoCopertina, cpu9607 as codicePubblicazione, num9607 as numeroCopertina, titolo9606 as titolo, sottot9607 as sottoTitolo, datausc9607 as dataUscita, coddl9607 as coddl, ciq9606 as codInizioQuotidiano, cfq9606 as codFineQuotidiano, to_char(sysdate,'dd-mm-yyyy') as strData from tbl_9607 inner join tbl_9606 on (coddl9606=coddl9607 and cpu9606=cpu9607) where coddl9607 in (:coddl) and datausc9607 = :today and trim(barcode9607) is not null :addWc union select idtn9607,barcode9607,prezzo9607,cpu9607,num9607,titolo9606,sottot9607,datausc9607,coddl9607,ciq9606,cfq9606,to_char(sysdate,'dd-mm-yyyy') from tbl_9607 inner join tbl_9606 on (coddl9606=coddl9607 and cpu9606=cpu9607) where coddl9607 in (:coddl) and datausc9607 > :cutoffDateSettimanali and trim(barcode9607) is not null and peridl9606 = " + IGerivConstants.PERIODICITA_SETTIMANALE + " :addWc union select idtn9607,barcode9607,prezzo9607,cpu9607,num9607,titolo9606,sottot9607,datausc9607,coddl9607,ciq9606,cfq9606,to_char(sysdate,'dd-mm-yyyy') from tbl_9607 inner join tbl_9606 on (coddl9606=coddl9607 and cpu9606=cpu9607) where coddl9607 in (:coddl) and datausc9607 > :cutoffDateMensili and trim(barcode9607) is not null and peridl9606 = " + IGerivConstants.PERIODICITA_MENSILE + " :addWc";
	public static final String SQL_QUERY_GET_UTLIME_PUBBLICAZIONI_PER_VENDITE_LOCALI = "select idtn9607 as idtn, barcode9607 as barcode, prezzo9607 as prezzoCopertina, cpu9607 as codicePubblicazione, num9607 as numeroCopertina, titolo9606 as titolo, sottot9607 as sottoTitolo, datausc9607 as dataUscita, coddl9607 as coddl, ciq9606 as codInizioQuotidiano, cfq9606 as codFineQuotidiano, to_char(sysdate,'dd-mm-yyyy') as strData from tbl_9607 inner join tbl_9606 on (coddl9606=coddl9607 and cpu9606=cpu9607) where coddl9607 in (:coddl) and datausc9607 = :today  :addWc union select idtn9607,barcode9607,prezzo9607,cpu9607,num9607,titolo9606,sottot9607,datausc9607,coddl9607,ciq9606,cfq9606,to_char(sysdate,'dd-mm-yyyy') from tbl_9607 inner join tbl_9606 on (coddl9606=coddl9607 and cpu9606=cpu9607) where coddl9607 in (:coddl) and datausc9607 > :cutoffDateSettimanali  and peridl9606 = " + IGerivConstants.PERIODICITA_SETTIMANALE + " :addWc union select idtn9607,barcode9607,prezzo9607,cpu9607,num9607,titolo9606,sottot9607,datausc9607,coddl9607,ciq9606,cfq9606,to_char(sysdate,'dd-mm-yyyy') from tbl_9607 inner join tbl_9606 on (coddl9606=coddl9607 and cpu9606=cpu9607) where coddl9607 in (:coddl) and datausc9607 > :cutoffDateMensili  and peridl9606 = " + IGerivConstants.PERIODICITA_MENSILE + " :addWc";
	public static final String SQL_QUERY_GET_UTLIME_PUBBLICAZIONI_PER_VENDITE_LOCALI_MULTI_DL_WHERE_CLAUSE = "and (exists (select bd_.coddl9611 as y0_ from tbl_9611 bd_ where bd_.coddl9611=coddl9606 and bd_.idtn9611=idtn9607 and bd_.crivw9611 in (:codEdicola)) or exists (select fbd_.coddl9612 as y0_  from tbl_9612 fbd_  where fbd_.coddl9612=coddl9606  and fbd_.idtn9612=idtn9607 and fbd_.crivw9612 in (:codEdicola) and fbd_.trfon9612=" + IGerivConstants.COD_TIPO_FONDO_BOLLA_RIFORNIMENTI + ") or exists (select rr_.coddl9131 as y0_ from tbl_9131 rr_ where rr_.coddl9131=coddl9606 and rr_.idtn9131=idtn9607 and rr_.crivw9131 in (:codEdicola) and rr_.flstat9131=" + IGerivConstants.COD_SI + ") or exists (select ss_.coddl9638 as y0_ from tbl_9638 ss_ where ss_.coddl9638=coddl9606 and ss_.idtn9638=idtn9607 and ss_.crivw9638 in (:codEdicola)))";
	public static final String SQL_QUERY_GET_STATISTICA_DETTAGLIO_RESO_RISCONTRATO = "select t1.datbr9635 as data, t1.tipbr9635 as tipoBolla, t1.qresor9635 as copie from tbl_9635 t1 where t1.coddl9635 in (:coddl) and t1.crivw9635 in (:codEdicola) and t1.idtn9635 = :idtn order by data asc";
	public static final String SQL_QUERY_GET_STATISTICA_DETTAGLIO_RESO = "select data, tipoBolla, copie from (select t1.datbr9621 as data, t1.tipbr9621 as tipoBolla, t1.qreso9621 as copie from tbl_9621 t1 where t1.coddl9621 in (:coddl) and t1.crivw9621 in (:codEdicola) and t1.idtn9621 = :idtn union all select t2.datbr9622 as data, t2.tipbr9622 as tipoBolla, t2.qreso9622 as copie from tbl_9622 t2 where t2.coddl9622 in (:coddl) and t2.crivw9622 in (:codEdicola) and t2.idtn9622 = :idtn union all select t3.datbr9622b as data, t3.tipbr9622b as tipoBolla, t3.qreso9622b as copie from tbl_9622b t3 where t3.coddl9622b in (:coddl) and t3.crivw9622b in (:codEdicola) and t3.idtn9622b = :idtn) order by data asc";
	public static final String SQL_QUERY_GET_STATISTICA_DETTAGLI_FORNITO = "select data, tipoBolla, tipoFondoBolla, copie from (select t1.datbc9611 as data, t1.tipbc9611 as tipoBolla, '' as tipoFondoBolla, t1.quant9611 as copie from tbl_9611 t1 where t1.coddl9611 in (:coddl) and t1.crivw9611 in (:codEdicola) and t1.idtn9611 = :idtn and ivalo9611 not in (" + IGerivConstants.INDICATORE_NON_VALORIZZARE + ") and datbc9611 > :dataStorico union all select t2.datbc9612 as data, t2.tipbc9612 as tipoBolla, t3.descr9614 as tipoFondoBolla, t2.quant9612 as copie from tbl_9612 t2 left outer join tbl_9614 t3 on t2.trfon9612 = t3.trfon9614 where t2.coddl9612  in (:coddl) and t2.crivw9612 in (:codEdicola) and t2.idtn9612 = :idtn and trfon9612 in (" + IGerivConstants.LIST_COD_TIPO_FONDO_BOLLA + ") and t2.datbc9612 > :dataStorico union all select t3.datarisp9131 as data, '' as tipoBolla, '' as tipoFondoBolla, t3.quaev9131 as copie from tbl_9131 t3 where t3.coddl9131 in (:coddl) and t3.crivw9131 in (:codEdicola) and t3.idtn9131 = :idtn and flstat9131 in (" + IGerivConstants.COD_SI + ") ) order by data asc";
	public static final String SQL_QUERY_GET_PREZZO = "select prezzo from (select t1.prezzo9508 - decode(t1.sconto9508, 0, t1.scontov9508, round(t1.prezzo9508 * t1.sconto9508 / 100, 2)) as prezzo from tbl_9508 t1 where t1.cpro9508 = :codProdotto and t1.coso9508 in (:codContabileCliente, 0) and t1.gs9508 = 0 and sysdate between t1.dvi9508 and t1.dvf9508 order by t1.coso9508 desc) where rownum < 2";
	public static final String SQL_QUERY_GET_MESSAGGIO_RIVENDITA = "select coddl9601 as codFiegDl, tdest9601 as tipoDestinatario, desta9601 as destinatarioA, destb9601 as destinatarioB, testo9601 as messaggio, testb9601 as messaggioEsteso, tipme9601 as tipoMessaggio, datam9601 as dtMessaggio, stato9601 as statoMessaggio, aleg19601 as attachmentName1, aleg29601 as attachmentName2, aleg39601 as attachmentName3, letto9602 as messaggioLetto from tbl_9601 left outer join tbl_9602 on coddl9602 = coddl9601 and datam9602 = datam9601 and coded9602 = :codEdicola where coddl9601 = :codDl and datam9601 = :dtMessaggio and tdest9601 = :tipoDestinatario and desta9601 = :destinatarioA and destb9601 = :destinatarioB";
	public static final String SQL_QUERY_GET_PK_MESSAGGIO_NON_LETTO = "select * from (select * from (select coddl9601 as codFiegDl, datam9601 as dtMessaggio, datas9601 as dtScadenzaMessaggio, tdest9601 as tipoDestinatario, desta9601 as destinatarioA, destb9601 as destinatarioB, tipme9601 as priorita, catmes9601 as categoria from tbl_9601 where coddl9601 = :codDl and tdest9601 = " + IGerivConstants.COD_TUTTE_LE_EDICOLE + " and desta9601 = :destinatarioA and destb9601 = :destinatarioB and stato9601 = " + IGerivConstants.STATO_MESSAGGIO_INVIATO + " and tipme9601 in (:tipoMessaggio) and not exists (select coded9602 from tbl_9602 where coddl9602 = coddl9601 and datam9602 = datam9601 and coded9602 = :codEdicola and letto9602 = " + IGerivConstants.COD_MESSAGGIO_LETTO + ") union all select coddl9601 as codFiegDl, datam9601 as dtMessaggio, datas9601 as dtScadenzaMessaggio, tdest9601 as tipoDestinatario, desta9601 as destinatarioA, destb9601 as destinatarioB, tipme9601 as priorita, catmes9601 as categoria from tbl_9601 where coddl9601 = :codDl and tdest9601 = " + IGerivConstants.COD_EDICOLA_SINGOLA + " and desta9601 = :destinatarioA1 and destb9601 = :destinatarioB1 and stato9601 = " + IGerivConstants.STATO_MESSAGGIO_INVIATO + " and tipme9601 in (:tipoMessaggio) and not exists (select coded9602 from tbl_9602 where coddl9602 = coddl9601 and datam9602 = datam9601 and coded9602 = :codEdicola and letto9602 = " + IGerivConstants.COD_MESSAGGIO_LETTO + ") union all select coddl9601 as codFiegDl, datam9601 as dtMessaggio, datas9601 as dtScadenzaMessaggio, tdest9601 as tipoDestinatario, desta9601 as destinatarioA, destb9601 as destinatarioB, tipme9601 as priorita, catmes9601 as categoria from tbl_9601 inner join tbl_9110 on coddl9110 = coddl9601 and crivw9110 = :codEdicola and gti9110 = desta9601 and giro9110 = destb9601 where coddl9601 = :codDl and tdest9601 = " + IGerivConstants.COD_GIRO_TIPO + " and stato9601 = " + IGerivConstants.STATO_MESSAGGIO_INVIATO + " and tipme9601 in (:tipoMessaggio) and not exists (select coded9602 from tbl_9602 where coddl9602 = coddl9601 and datam9602 = datam9601 and coded9602 = :codEdicola and letto9602 = " + IGerivConstants.COD_MESSAGGIO_LETTO + ") union all select coddl9601 as codFiegDl, datam9601 as dtMessaggio, datas9601 as dtScadenzaMessaggio, tdest9601 as tipoDestinatario, desta9601 as destinatarioA, destb9601 as destinatarioB, tipme9601 as priorita, catmes9601 as categoria from tbl_9601 inner join tbl_9111 on coddl9111 = coddl9601 and crivw9111 = :codEdicola and zti9111 = desta9601 and zona9111 = destb9601 where coddl9601 = :codDl and tdest9601 = " + IGerivConstants.COD_ZONA_TIPO + " and stato9601 = " + IGerivConstants.STATO_MESSAGGIO_INVIATO + " and tipme9601 in (:tipoMessaggio) and not exists (select coded9602 from tbl_9602 where coddl9602 = coddl9601 and datam9602 = datam9601 and coded9602 = :codEdicola and letto9602 = " + IGerivConstants.COD_MESSAGGIO_LETTO + ") ) where dtMessaggio >= :dtAttivazioneEdicola and (sysdate between dtMessaggio and decode(dtScadenzaMessaggio, null, (sysdate + 1), dtScadenzaMessaggio)) order by priorita desc, dtMessaggio desc) where rownum <= 1";
