	public static final String SQL_GET_ORDINI_IN_BOLLA_NON_EVASI = "select * from (select t1.*, nvl((select quant9611 from tbl_9611 where datbc9611 = datbc9609 and tipbc9611 = tipbc9609 and coddl9611 = coddl9609 and crivw9611 = :codEdicola and posiz9611 = posiz9609),0) as quantitaInBolla from (select datbc9609, tipbc9609, coddl9609, titolo9609 as titolo, sottot9609 as sottotitolo, num9609 as numero, idtn9609 as idtn, posiz9609, sum(quar9310) as quantitaRichiesta from tbl_9609 inner join tbl_9310 on (coddl9609=codl9310 and crivw9310=:codEdicola and idtn9609=idtn9310) where datbc9609=:dtBolla and tipbc9609 = :tipoBolla and coddl9609 = :coddl group by datbc9609,tipbc9609,coddl9609,titolo9609,sottot9609,num9609,idtn9609,posiz9609) t1) where quantitaRichiesta > quantitaInBolla";
	public static final String SQL_GET_PUBBLICAZIONI_INVENDUTE_SU_RESE = "select * from (select ap2_.titolo9606 as titolo, sc1_.cpu9607 as codicePubblicazione, sc1_.sottot9607 as sottoTitolo, sc1_.num9607 as numeroCopertina, sc1_.prezzo9607 as prezzoCopertina, sc1_.datausc9607 as dataUscita, sc1_.idtn9607 as idtn, sc1_.coddl9607 as coddl, (select t5.prnet9617 from tbl_9617 t5 where t5.coddl9617 = sc1_.coddl9607 and t5.idtn9617 = sc1_.idtn9607 and t5.gs9617 = :gruppoSconto) as prezzoEdicola, arg4_.descr9217 as argomento, per5_.descr9216 as periodicita, per5_.tipop9216 || '|' || per5_.perio9216 as periodicitaPk, im3_.nomei9700 as immagine, (select t2.noter9640 from tbl_9640 t2 where t2.idtn9640 = this_.idtn9611 and t2.crivw9640 = this_.crivw9611) as note, (select t2.noter9641 from tbl_9641 t2 where t2.cpu9641 = (select t3.cpu9607 from tbl_9607 t3 where t3.coddl9607 = this_.coddl9611 and t3.idtn9607 = this_.idtn9611) and t2.crivw9641 = this_.crivw9611) as noteByCpu, (P_BOLLA.GET_FORNITO(this_.coddl9611,this_.crivw9611,this_.idtn9611)) as fornitoSP, this_.crivw9611 as crivw from tbl_9611 this_ inner join tbl_9607 sc1_ on this_.coddl9611=sc1_.coddl9607 and this_.idtn9611=sc1_.idtn9607 inner join tbl_9606 ap2_ on sc1_.coddl9607=ap2_.coddl9606 and sc1_.cpu9607=ap2_.cpu9606 left outer join tbl_9217 arg4_ on ap2_.coddl9606=arg4_.coddl9217 and ap2_.argodl9606=arg4_.segm9217 left outer join tbl_9216 per5_ on ap2_.peridl9606=per5_.perio9216 and 1=per5_.tipop9216 left outer join tbl_9700 im3_ on sc1_.barcode9607=im3_.barcode9700 where this_.coddl9611 in (:coddl) and this_.crivw9611 in (:codEdicola) and sc1_.datausc9607>=:dataDa and sc1_.datausc9607<=:dataA $1 $2 $3) t where (t.fornitoSP) = (select max((P_BOLLA.GET_RESO(t.coddl, t.crivw, t.idtn))) as y0_ from tbl_9611 vd_ where vd_.coddl9611 = t.coddl and vd_.crivw9611 = t.crivw and vd_.idtn9611 = t.idtn) order by t.titolo asc, t.dataUscita desc";
